package Lecture1;

public class Arithmetic_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =10;
		int b= 7;
		int c = a+b;
		System.out.println(c);
		c= a-b;
		System.out.println(c);
		c = c+7;
		System.out.println(c);  
		c = a*b;
		System.out.println(c);
		c = a/b;
		System.out.println(c);
		c = b/a;
		System.out.println(c);
		c = b%a;
		System.out.println(c);
		c= a%b;
		System.out.println(c);
		
		
		

	}

}
